import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    // Blog yazılarını burada API'den çekebilirsiniz
    const samplePosts = [
      { id: 1, title: "React ile Blog Yapmak", content: "React ile nasıl blog yapılır..." },
      { id: 2, title: "JavaScript Temelleri", content: "JavaScript öğrenmeye başlamak..." },
    ];
    setPosts(samplePosts);
  }, []);

  return (
    <div>
      <h1>Blog Yazıları</h1>
      <ul>
        {posts.map(post => (
          <li key={post.id}>
            <Link to={`/post/${post.id}`}>
              <h2>{post.title}</h2>
            </Link>
            <p>{post.content.slice(0, 100)}...</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
